import React from 'react'
import PropTypes from 'prop-types'
import { Image, Spin } from 'antd'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
  faUserPlus,
  faUsers,
  faCode,
  faLocationArrow
} from '@fortawesome/free-solid-svg-icons'

export default function Card(props) {
  const { status, avatar_url, location, following, followers, html_url, public_repos } =
    props
  
  return (
    <a
      href={html_url}
      target="_blank"
      className="db br1 bg-light-gray mb4 pv4 shadow-hover no-underline color-inherit w-80 center"
      rel="noreferrer"
    >
      <div className="flex flex-column justify-center items-center">
        <h2 className="mb2 mt0">{status}</h2>
        <Image
          src={avatar_url}
          preview={false}
          width="120px"
          height="120px"
          placeholder={
            <div className="w-100 h-100 tc">
              <Spin />
            </div>
          }
          fallback="data:image/png;base64,"
        />
        <h4 className="f4 mt3 mb1 red">Scores: {public_repos}</h4>
        <div className="ph4 tl w-100 f5">
          <p className="mv2 flex items-center" title={location}>
            <FontAwesomeIcon icon={faLocationArrow} color="#ffbf74" fixedWidth />
            <span className="ml2 flex-auto-l truncate">{location}</span>
          </p>
          <p className="mv2 flex items-center">
            <FontAwesomeIcon icon={faUsers} color="#ffd701" fixedWidth />
            <span className="ml2">{followers}</span>
          </p>
          <p className="mv2 flex items-center">
            <FontAwesomeIcon icon={faUserPlus} color="#8dc6f3" fixedWidth />
            <span className="ml2">{following}</span>
          </p>
          <p className="mv2 flex items-center">
            <FontAwesomeIcon
              icon={faCode}
              color="#f18d95"
              fixedWidth
            />
            <span className="ml2">{public_repos}</span>
          </p>
        </div>
      </div>
    </a>
  )
}

Card.propTypes = {
  // 胜负状态
  status: PropTypes.string,
  location: PropTypes.string,
  avatar_url: PropTypes.string.isRequired,
  following: PropTypes.number.isRequired,
  followers: PropTypes.number.isRequired,
  html_url: PropTypes.string.isRequired,
  public_repos: PropTypes.number.isRequired,
}

Card.STATUS = {
  WIN: 'Winner',
  LOSE: 'Loser',
  TIE: 'Tie'
}
